  const timingInfo = fetchParams.timingInfo

  // 16. Set timingInfo‚Äôs redirect end time and post-redirect start time to the
  // coarsened shared current time given fetchParams‚Äôs cross-origin isolated
  // capability.
  timingInfo.redirectEndTime = timingInfo.postRedirectStartTime =
    coarsenedSharedCurrentTime(fetchParams.crossOriginIsolatedCapability)

  // 17. If timingInfo‚Äôs redirect start time is 0, then set timingInfo‚Äôs
  //  redirect start time to timingInfo‚Äôs start time.
  if (timingInfo.redirectStartTime === 0) {
    timingInfo.redirectStartTime = timingInfo.startTime
  }

  // 18. Append locationURL to request‚Äôs URL list.
  request.urlList.push(locationURL)

  // 19. Invoke set request‚Äôs referrer policy on redirect on request and
  // actualResponse.
  setRequestReferrerPolicyOnRedirect(request, actualResponse)

